namespace Prestafy\Rede\Model\ResourceModel\Transaction;

use Magento\Framework\Model\ResourceModel\Db\Collection\AbstractCollection;
use Prestafy\Rede\Model\Transaction;
use Prestafy\Rede\Model\ResourceModel\Transaction as TransactionResource;

class Collection extends AbstractCollection
{
    protected $_idFieldName = 'id';

    public function _construct()
    {
        $this->_init(
            Transaction::class,
            TransactionResource::class
        );
    }
}

namespace Prestafy\Rede\Model\ResourceModel;

use Magento\Framework\Model\ResourceModel\Db\AbstractDb;

class Transaction extends AbstractDb
{
    protected function _construct()
    {
        $this->_init('prestafy_rede_transactions', 'id');
    }
}

namespace Prestafy\Rede\Model;

use Magento\Framework\Model\AbstractModel;
use Prestafy\Rede\Model\ResourceModel\Transaction as TransactionResource;

class Transaction extends AbstractModel
{
    public function _construct()
    {
        $this->_init(TransactionResource::class);
    }
}
