namespace Prestafy\Rede\Model\ResourceModel\Transaction;

use Magento\Framework\Model\ResourceModel\Db\Collection\AbstractCollection;
use Prestafy\Rede\Model\Transaction;
use Prestafy\Rede\Model\ResourceModel\Transaction as TransactionResource;

class Collection extends AbstractCollection
{
    protected $_idFieldName = 'id';

    public function _construct()
    {
        $this->_init(
            Transaction::class,
            TransactionResource::class
        );
    }
}

namespace Prestafy\Rede\Model\ResourceModel;

use Magento\Framework\Model\ResourceModel\Db\AbstractDb;

class Transaction extends AbstractDb
{
    protected function _construct()
    {
        $this->_init('prestafy_rede_transactions', 'id');
    }
}

namespace Prestafy\Rede\Model;

use Magento\Framework\Model\AbstractModel;
use Prestafy\Rede\Model\ResourceModel\Transaction as TransactionResource;

class Transaction extends AbstractModel
{
    public function _construct()
    {
        $this->_init(TransactionResource::class);
    }
}

namespace Prestafy\Rede\Setup;

use Magento\Framework\Setup\InstallSchemaInterface;
use Magento\Framework\Setup\ModuleContextInterface;
use Magento\Framework\Setup\SchemaSetupInterface;
use Magento\Framework\DB\Ddl\Table;

class InstallSchema implements InstallSchemaInterface
{
    /**
     * @param SchemaSetupInterface $setup
     * @param ModuleContextInterface $context
     */
    public function install(SchemaSetupInterface $setup, ModuleContextInterface $context)
    {
        $setup->startSetup();

        $table = $setup->getConnection()->newTable(
            $setup->getTable('prestafy_rede_transactions')
        )->addColumn(
            'id',
            Table::TYPE_INTEGER,
            null,
            ['identity' => true, 'nullable' => false, 'primary' => true],
            'Transaction PK'
        )->addColumn(
            'transaction',
            Table::TYPE_TEXT,
            100,
            ['nullable' => false],
            'Rede Transaction ID'
        )->addColumn(
            'status',
            Table::TYPE_INTEGER,
            null,
            ['nullable' => false],
            'Rede Return Status'
        )->addColumn(
            'order_id',
            Table::TYPE_INTEGER,
            null,
            ['nullable' => false, 'unsigned' => true],
            'Magento Order Entity ID'
        )->addColumn(
            'installments',
            Table::TYPE_SMALLINT,
            null,
            ['nullable' => false, 'unsigned' => true],
            'Number of installments'
        )->addColumn(
            'installment_value',
            Table::TYPE_DECIMAL,
            null,
            ['nullable' => false],
            'Value of each installment'
        )
            ->addForeignKey(
                $setup->getFkName(
                    'prestafy_rede_transactions',
                    'order_id',
                    'sales_order',
                    'entity_id'
                ),
                'order_id',
                $setup->getTable('sales_order'),
                'entity_id',
                Table::ACTION_CASCADE
            )->setComment('e-Rede Transactions');

        $setup->getConnection()->createTable($table);
        $setup->endSetup();
    }
}
